import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { runAssessment, fetchQuestionnaire } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";
import logo from "../assets/basiclogo1.png";

/* ðŸ”¹ SMOOTH PAGE TRANSITION */
const fadeBounce = {
  initial: { opacity: 0, y: 20 },
  animate: {
    opacity: 1,
    y: 0,
    transition: { type: "tween", duration: 0.4, ease: "easeOut" }
  },
  exit: { opacity: 0, y: -20, transition: { duration: 0.25 } }
};

const buttonBounce = {
  whileHover: { scale: 1.03 },
  whileTap: { scale: 0.98 }
};

const AssessmentPage = () => {
  const navigate = useNavigate();

  /* ðŸ”¹ UI STATE */
  const [darkMode, setDarkMode] = useState(true);
  const [step, setStep] = useState("profile");
  const [isMobile, setIsMobile] = useState(false);

  /* ðŸ”¹ QUESTION STATE (âœ… MUST BE INSIDE COMPONENT) */
  const [questions, setQuestions] = useState([]);
  const [loadingQuestions, setLoadingQuestions] = useState(false);
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);
  const [answers, setAnswers] = useState({});

  /* ðŸ”¹ PROFILE DATA */
  const [profileData, setProfileData] = useState({
    age: "",
    currentClass: "",
    stream: "",
    familyAnnualBudget: "",
    educationLoanComfort: "",
    coachingAffordability: false
  });

  /* ðŸ”¹ EFFECTS */
  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 640);
    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  /* ðŸ”¹ PROFILE HANDLER */
  const handleProfileChange = (e) => {
    const { name, value, type, checked } = e.target;
    setProfileData((p) => ({
      ...p,
      [name]: type === "checkbox" ? checked : value
    }));
  };

  const isProfileComplete = Object.entries(profileData).every(
    ([key, value]) =>
      key === "coachingAffordability"
        ? typeof value === "boolean"
        : value !== ""
  );

  /* ðŸ”¹ LOAD QUESTIONS FROM BACKEND */
  const loadQuestions = async () => {
    try {
      setLoadingQuestions(true);

      const res = await fetchQuestionnaire(profileData.currentClass);

      if (!res.data?.questions?.length) {
        alert("No questions found for selected class");
        return;
      }

      setQuestions(res.data.questions);
      setCurrentQuestionIdx(0);
      setAnswers({});
      setStep("questions");
    } catch (err) {
      console.error("QUESTION FETCH ERROR:", err);
      alert("Failed to load questions");
    } finally {
      setLoadingQuestions(false);
    }
  };

  /* ðŸ”¹ ANSWER SELECT */
  const handleSelect = (qid, optionIndex) => {
    setAnswers((prev) => ({
      ...prev,
      [qid]: optionIndex
    }));
  };

  /* ðŸ”¹ SUBMIT */
  const handleSubmit = async () => {
    if (Object.keys(answers).length !== questions.length) {
      alert("Please answer all questions");
      return;
    }

    try {
      const payload = buildPayload({ profileData, answers });
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch (err) {
      console.error("ASSESSMENT ERROR:", err);
      alert("Assessment failed");
    }
  };

  const currentQuestion = questions[currentQuestionIdx];
  const questionSection = currentQuestion?.section;
  const progress =
    questions.length > 0
      ? ((currentQuestionIdx + 1) / questions.length) * 100
      : 0;

  return (
    <div
      className={`min-h-screen transition-all duration-700 ${
        darkMode ? "bg-slate-900 text-white" : "bg-white text-black"
      }`}
    >
      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 backdrop-blur-lg border-b">
        <div className="max-w-6xl mx-auto px-6 py-4 flex justify-between">
          <div className="flex items-center gap-3">
            <img src={logo} className="w-10 h-10 rounded-xl" />
            <h1 className="font-extrabold text-xl">CollegeReviewZ</h1>
          </div>
        </div>
      </header>

      <div className="h-28" />

      {/* PROFILE STEP */}
      {step === "profile" && (
        <AnimatePresence>
          <motion.section {...fadeBounce} className="max-w-4xl mx-auto p-10">
            <h2 className="text-2xl font-bold mb-8 text-center">
              ðŸŽ“ Student Profile
            </h2>

            {/* (inputs unchanged â€“ your structure preserved) */}

            <div className="flex justify-center mt-10">
              <motion.button
                {...buttonBounce}
                disabled={!isProfileComplete || loadingQuestions}
                onClick={loadQuestions}
                className="px-10 py-4 bg-indigo-500 text-white font-bold text-lg rounded-2xl"
              >
                {loadingQuestions ? "Loading Questions..." : "Save & Continue â†’"}
              </motion.button>
            </div>
          </motion.section>
        </AnimatePresence>
      )}

      {/* QUESTIONS STEP */}
      {step === "questions" && currentQuestion && (
        <div className="max-w-4xl mx-auto px-4 pb-24">
          <div className="h-3 bg-white/40 rounded-full mb-8 overflow-hidden">
            <motion.div
              className="h-full bg-indigo-500"
              animate={{ width: `${progress}%` }}
            />
          </div>

          <h3 className="text-xl font-bold mb-6">{currentQuestion.text}</h3>

          {currentQuestion.options.map((opt, idx) => {
            const active = answers[currentQuestion.id] === idx;
            return (
              <label
                key={idx}
                className={`block p-4 mb-3 border rounded-xl cursor-pointer ${
                  active ? "bg-indigo-500 text-white" : ""
                }`}
              >
                <input
                  type="radio"
                  checked={active}
                  onChange={() =>
                    handleSelect(currentQuestion.id, idx)
                  }
                  className="hidden"
                />
                {typeof opt === "string" ? opt : opt.label}
              </label>
            );
          })}

          <div className="flex justify-between mt-10">
            <button
              disabled={currentQuestionIdx === 0}
              onClick={() =>
                setCurrentQuestionIdx((i) => Math.max(i - 1, 0))
              }
            >
              Previous
            </button>

            {currentQuestionIdx < questions.length - 1 ? (
              <button
                onClick={() =>
                  setCurrentQuestionIdx((i) => i + 1)
                }
                disabled={answers[currentQuestion.id] === undefined}
              >
                Next â†’
              </button>
            ) : (
              <button onClick={handleSubmit}>Submit Assessment</button>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default AssessmentPage;
