import React, { useState } from "react";
import AnimatedPageWrapper from "../components/AnimatedPageWrapper";
import ProfileForm from "../components/ProfileForm";
import Questionnaire from "../components/Questionnaire";
import ResultScreen from "../components/ResultScreen";
import { fetchQuestionnaire, runAssessment } from "../api/assessment.api";
import { buildPayload } from "../utils/payloadBuilder";
import Loader from "../components/Loader";

const AssessmentPage = () => {
  const [step, setStep] = useState("profile");
  const [profileData, setProfileData] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);

  /* STEP 1: PROFILE SUBMIT */
  const handleProfileSubmit = async (profile) => {
    setLoading(true);
    setProfileData(profile);

    try {
      const res = await fetchQuestionnaire(profile.currentClass);
      setQuestions(res.data.questions);
      setStep("questions");
    } catch (err) {
      alert("Failed to load questions");
    } finally {
      setLoading(false);
    }
  };

  /* STEP 2: QUESTION SUBMIT */
  const handleAssessmentSubmit = async (answers) => {
    setLoading(true);
    setAnswers(answers);

    try {
      const payload = buildPayload({ profileData, answers });
      const res = await runAssessment(payload);
      setResult(res.data);
      setStep("result");
    } catch (err) {
      alert("Assessment failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <AnimatedPageWrapper>
      {loading && <Loader />}

      {step === "profile" && (
        <ProfileForm onSubmit={handleProfileSubmit} />
      )}

      {step === "questions" && (
        <Questionnaire
          questions={questions}
          onSubmit={handleAssessmentSubmit}
        />
      )}

      {step === "result" && <ResultScreen data={result} />}
    </AnimatedPageWrapper>
  );
};

export default AssessmentPage;
