import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { runAssessment, fetchQuestionnaire } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";
import { buildPayload } from "../utils/payloadBuilder";
import logo from "../assets/basiclogo1.png";

/* Animations */
const fadeBounce = {
  initial: { opacity: 0, y: 20 },
  animate: { opacity: 1, y: 0, transition: { duration: 0.4 } },
  exit: { opacity: 0, y: -20, transition: { duration: 0.25 } }
};

const buttonBounce = {
  whileHover: { scale: 1.03 },
  whileTap: { scale: 0.98 }
};

const AssessmentPage = () => {
  const navigate = useNavigate();

  /* UI STATE */
  const [step, setStep] = useState("profile");
  const [darkMode, setDarkMode] = useState(true);

  /* PROFILE */
  const [profileData, setProfileData] = useState({
    age: "",
    currentClass: "",
    stream: "",
    familyAnnualBudget: "",
    educationLoanComfort: "",
    coachingAffordability: false
  });

  /* QUESTIONS (âœ… MUST BE INSIDE COMPONENT) */
  const [questions, setQuestions] = useState([]);
  const [loadingQuestions, setLoadingQuestions] = useState(false);
  const [currentQuestionIdx, setCurrentQuestionIdx] = useState(0);
  const [answers, setAnswers] = useState({});

  useEffect(() => {
    document.documentElement.classList.toggle("dark", darkMode);
  }, [darkMode]);

  /* PROFILE HANDLER */
  const handleProfileChange = (e) => {
    const { name, value, type, checked } = e.target;
    setProfileData(p => ({
      ...p,
      [name]: type === "checkbox" ? checked : value
    }));
  };

  const isProfileComplete = Object.entries(profileData).every(
    ([k, v]) => k === "coachingAffordability" ? true : v !== ""
  );

  /* ðŸ”¥ FETCH QUESTIONS FROM BACKEND */
  const loadQuestions = async () => {
    try {
      setLoadingQuestions(true);

      const res = await fetchQuestionnaire(profileData.currentClass);

      if (!res.data?.questions?.length) {
        alert("No questions found for selected class");
        return;
      }

      setQuestions(res.data.questions);
      setAnswers({});
      setCurrentQuestionIdx(0);
      setStep("questions");
    } catch (err) {
      console.error(err);
      alert("Failed to load questions");
    } finally {
      setLoadingQuestions(false);
    }
  };

  /* ANSWER SELECT */
  const handleSelect = (qid, idx) => {
    setAnswers(a => ({ ...a, [qid]: idx }));
  };

  /* SUBMIT */
  const handleSubmit = async () => {
    if (Object.keys(answers).length !== questions.length) {
      alert("Answer all questions");
      return;
    }

    const payload = buildPayload({ profileData, answers });
    const res = await runAssessment(payload);
    navigate("/report", { state: res.data });
  };

  const currentQuestion = questions[currentQuestionIdx];
  const progress =
    questions.length > 0
      ? ((currentQuestionIdx + 1) / questions.length) * 100
      : 0;

  return (
    <div className={`min-h-screen ${darkMode ? "bg-slate-900 text-white" : "bg-white text-black"}`}>
      {/* HEADER */}
      <header className="fixed top-0 w-full z-50 backdrop-blur border-b">
        <div className="max-w-6xl mx-auto px-6 py-4 flex items-center gap-3">
          <img src={logo} className="w-10 h-10 rounded-xl" />
          <h1 className="font-bold text-xl">CollegeReviewZ</h1>
        </div>
      </header>

      <div className="h-28" />

      {/* PROFILE */}
      {step === "profile" && (
        <motion.section {...fadeBounce} className="max-w-4xl mx-auto p-10">
          <h2 className="text-2xl font-bold text-center mb-8">ðŸŽ“ Student Profile</h2>

          {/* YOUR INPUTS (UNCHANGED) */}

          <div className="flex justify-center mt-10">
            <motion.button
              {...buttonBounce}
              disabled={!isProfileComplete || loadingQuestions}
              onClick={loadQuestions}
              className="px-10 py-4 bg-indigo-500 text-white rounded-xl"
            >
              {loadingQuestions ? "Loading Questions..." : "Save & Continue â†’"}
            </motion.button>
          </div>
        </motion.section>
      )}

      {/* QUESTIONS */}
      {step === "questions" && currentQuestion && (
        <div className="max-w-4xl mx-auto px-4">
          <div className="h-2 bg-gray-300 rounded mb-6">
            <div className="h-full bg-indigo-500" style={{ width: `${progress}%` }} />
          </div>

          <h3 className="text-xl font-bold mb-6">{currentQuestion.text}</h3>

          {currentQuestion.options.map((opt, idx) => (
            <label
              key={idx}
              className={`block p-4 mb-3 border rounded-xl cursor-pointer ${
                answers[currentQuestion.id] === idx ? "bg-indigo-500 text-white" : ""
              }`}
            >
              <input
                type="radio"
                className="hidden"
                checked={answers[currentQuestion.id] === idx}
                onChange={() => handleSelect(currentQuestion.id, idx)}
              />
              {typeof opt === "string" ? opt : opt.label}
            </label>
          ))}

          <div className="flex justify-between mt-8">
            <button
              disabled={currentQuestionIdx === 0}
              onClick={() => setCurrentQuestionIdx(i => i - 1)}
            >
              Previous
            </button>

            {currentQuestionIdx < questions.length - 1 ? (
              <button onClick={() => setCurrentQuestionIdx(i => i + 1)}>
                Next â†’
              </button>
            ) : (
              <button onClick={handleSubmit}>Submit</button>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default AssessmentPage;
