import { useLocation, useNavigate } from "react-router-dom";

const ReportPage = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  if (!state) {
    return (
      <div style={{ padding: 20 }}>
        <p>No report found.</p>
        <button onClick={() => navigate("/")}>Go Back</button>
      </div>
    );
  }

  return (
    <div style={{ padding: 20 }}>
      <h1>Assessment Result</h1>

      <p><strong>Global Score:</strong> {state.globalScore}</p>

      {state.careers.map(c => (
        <div key={c.id} style={{ marginBottom: 20 }}>
          <h3>{c.name}</h3>
          <p>Tier: {c.tier}</p>
          <p>Compatibility: {c.compatibilityScore}%</p>
        </div>
      ))}

      <a href={`http://localhost:5000/${state.reportPath}`} download>
        Download PDF Report
      </a>
    </div>
  );
};

export default ReportPage;
