import React, { useState } from 'react';
import { jsPDF } from 'jspdf';
import autoTable from 'jspdf-autotable';
import questionnaireDefinition from './serverData'; 
// import axios from 'axios'; // We can skip axios if generating locally

const App = () => {
  // --- STATE ---
  const [step, setStep] = useState('info'); 
  const [currentIdx, setCurrentIdx] = useState(0);
  const [answers, setAnswers] = useState({});
  const [studentDetails, setStudentDetails] = useState({
    fullName: '',
    stream: 'Science(PCM)',
    budget: '1-4'
  });

  const questions = questionnaireDefinition;

  // --- SECTION RANGES ---
  const sections = [
    { name: "COGNITIVE", range: [0, 11] },
    { name: "NUMERACY", range: [12, 19] },
    { name: "ACADEMIC", range: [20, 29] },
    { name: "VERBAL", range: [30, 35] },
    { name: "INTEREST", range: [36, 43] },
    { name: "DISCIPLINE", range: [44, 51] },
    { name: "RISK", range: [52, 59] }
  ];

  const currentSection = sections.find(s => currentIdx >= s.range[0] && currentIdx <= s.range[1]);

  // --- NAVIGATION LOGIC ---
  const handleNext = () => {
    if (currentIdx < questions.length - 1) {
      setCurrentIdx(prev => prev + 1);
    } else {
      setStep('result');
    }
  };

  const handlePrevious = () => {
    if (currentIdx > 0) setCurrentIdx(prev => prev - 1);
  };

  const handleSkip = () => {
    if (currentIdx === currentSection.range[1]) {
      handleSaveAndNextSection();
    } else {
      handleNext();
    }
  };

  const handleSaveAndNextSection = () => {
    const sectionIdx = sections.indexOf(currentSection);
    if (sectionIdx < sections.length - 1) {
      setCurrentIdx(sections[sectionIdx + 1].range[0]);
    } else {
      setStep('result');
    }
  };

  const onOptionSelect = (qId, optIdx) => {
    setAnswers({ ...answers, [qId]: optIdx });
    if (currentIdx !== currentSection.range[1]) {
      handleNext();
    }
  };

  // --- UPDATED PDF DOWNLOAD LOGIC ---
  // This now works directly without needing a backend server
  const downloadPDF = () => {
    try {
      const doc = new jsPDF();
      
      // 1. Header
      doc.setFontSize(22);
      doc.setTextColor(26, 115, 232);
      doc.text("Career Assessment Report", 14, 20);
      
      // 2. Student Details
      doc.setFontSize(12);
      doc.setTextColor(100);
      doc.text(`Name: ${studentDetails.fullName}`, 14, 32);
      doc.text(`Stream: ${studentDetails.stream}`, 14, 38);
      doc.text(`Budget Preference: ${studentDetails.budget} Lakhs`, 14, 44);
      doc.text(`Date: ${new Date().toLocaleDateString()}`, 14, 50);

      // 3. Summary Table (Example Recommendations based on stream)
      const recommendations = [
        [studentDetails.stream.includes("PCM") ? "Software Engineering" : "Medical Sciences", "4 Years", studentDetails.budget + " Lakhs", "High"],
        ["Data Science", "3 Years", "2-5 Lakhs", "Very High"],
        ["Management", "3 Years", "3-6 Lakhs", "Medium"]
      ];

      autoTable(doc, {
        startY: 60,
        head: [['Recommended Path', 'Duration', 'Est. Fees', 'Market Demand']],
        body: recommendations,
        theme: 'striped',
        headStyles: { fillColor: [26, 115, 232] }
      });

      // 4. Footer
      doc.setFontSize(10);
      doc.text("This is an AI-generated career recommendation report.", 14, doc.internal.pageSize.height - 10);

      doc.save(`${studentDetails.fullName}_Career_Report.pdf`);
    } catch (err) {
      console.error("PDF Generation Error:", err);
      alert("Error generating PDF. Please check the console.");
    }
  };

  return (
    <div style={styles.container}>
      {/* 1. STUDENT INFO */}
      {step === 'info' && (
        <div style={styles.card}>
          <h2 style={styles.title}>Student Information</h2>
          <div style={styles.field}>
            <label>Full Name *</label>
            <input 
              style={styles.input} 
              placeholder="Enter your name" 
              value={studentDetails.fullName}
              onChange={e => setStudentDetails({...studentDetails, fullName: e.target.value})} 
            />
          </div>
          <div style={styles.field}>
            <label>Stream</label>
            <select style={styles.input} value={studentDetails.stream} onChange={e => setStudentDetails({...studentDetails, stream: e.target.value})}>
              <option value="Science(PCM)">Science(PCM)</option>
              <option value="Science(PCB)">Science(PCB)</option>
              <option value="Commerce">Commerce</option>
              <option value="Arts">Arts</option>
            </select>
          </div>
          <div style={styles.field}>
            <label>Budget (Lakhs)</label>
            <select style={styles.input} value={studentDetails.budget} onChange={e => setStudentDetails({...studentDetails, budget: e.target.value})}>
              <option value="1-4">1-4 Lakhs</option>
              <option value="4-7">4-7 Lakhs</option>
              <option value="7-15">7-15 Lakhs</option>
            </select>
          </div>
          <button style={styles.btnPrimary} onClick={() => studentDetails.fullName ? setStep('start') : alert("Full Name is Required")}>
            Continue
          </button>
        </div>
      )}

      {/* 2. START ASSESSMENT */}
      {step === 'start' && (
        <div style={styles.card}>
          <h1 style={styles.title}>Start Assessment</h1>
          <p>Ready, {studentDetails.fullName}? You have 60 questions ahead.</p>
          <button style={styles.btnPrimary} onClick={() => setStep('quiz')}>Start Assessment Now</button>
        </div>
      )}

      {/* 3. 60-QUESTION QUIZ */}
      {step === 'quiz' && (
        <div style={styles.card}>
          <div style={styles.topBar}>
            <span style={styles.badge}>{currentSection.name}</span>
            <span style={styles.progressText}>{currentIdx + 1} / 60</span>
          </div>
          <h3 style={styles.qText}>{questions[currentIdx]?.text}</h3>
          
          <div style={styles.optionsGrid}>
            {questions[currentIdx]?.options.map((opt, i) => (
              <button 
                key={i} 
                style={{
                  ...styles.optBtn, 
                  backgroundColor: answers[questions[currentIdx].id] === i ? '#e8f0fe' : '#fff',
                  border: answers[questions[currentIdx].id] === i ? '2px solid #1a73e8' : '1px solid #eee'
                }}
                onClick={() => onOptionSelect(questions[currentIdx].id, i)}
              >
                {typeof opt === 'object' ? opt.label : opt}
              </button>
            ))}
          </div>

          <div style={{textAlign: 'right', marginTop: '10px'}}>
             <button style={styles.btnSkip} onClick={handleSkip}>Skip Question</button>
          </div>

          <div style={styles.footer}>
            <button style={styles.btnSecondary} onClick={handlePrevious} disabled={currentIdx === 0}>← Previous</button>
            
            {currentIdx === currentSection.range[1] ? (
              <button style={styles.btnSuccess} onClick={handleSaveAndNextSection}>Save & Next Section</button>
            ) : (
              <button style={styles.btnSecondary} onClick={handleNext}>Next →</button>
            )}
          </div>
        </div>
      )}

      {/* 4. DOWNLOAD PDF */}
      {step === 'result' && (
        <div style={styles.card}>
          <h2 style={{color: '#28a745'}}>Assessment Complete!</h2>
          <p>Thank you for participating. Your results are ready.</p>
          <button style={styles.btnDownload} onClick={downloadPDF}>
            Download PDF Report
          </button>
        </div>
      )}
    </div>
  );
};

