import React, { useState } from "react";
import questions from "../data/questions";
import { runAssessment } from "../api/assessment.api";
import { useNavigate } from "react-router-dom";

const AssessmentPage = () => {
  const [answers, setAnswers] = useState({});
  const navigate = useNavigate();

  const handleSelect = (qid, optionIndex) => {
    setAnswers(prev => ({
      ...prev,
      [qid]: optionIndex
    }));
  };

  const handleSubmit = async () => {
    if (Object.keys(answers).length < questions.length) {
      alert("Please answer all questions");
      return;
    }

    const payload = {
      studentId: "TEST123",
      profileData: {
        currentClass: "Class 10",
        stream: "Science",
        familyAnnualBudget: "3–6 Lakh"
      },
      answers
    };

    try {
      const res = await runAssessment(payload);
      navigate("/report", { state: res.data });
    } catch (err) {
      alert("Assessment failed");
    }
  };

  return (
    <div style={{ padding: 20, maxWidth: 900, margin: "auto" }}>
      <h1>Career Assessment</h1>

      {questions.map((q) => (
        <div key={q.id} style={{ marginBottom: 25 }}>
          <p><strong>{q.id}. {q.text}</strong></p>

          {q.options.map((opt, idx) => {
            const label =
              typeof opt === "string" ? opt : opt.label;

            return (
              <label key={idx} style={{ display: "block", marginBottom: 5 }}>
                <input
                  type="radio"
                  name={q.id}
                  checked={answers[q.id] === idx}
                  onChange={() => handleSelect(q.id, idx)}
                />
                {" "}{label}
              </label>
            );
          })}
        </div>
      ))}

      <button onClick={handleSubmit} style={{ padding: "10px 20px" }}>
        Submit Assessment
      </button>
    </div>
  );
};

export default AssessmentPage;
